var nodemailer = require('nodemailer');

var transporter = nodemailer.createTransport({
  service: 'gmail',
  auth: {
    user: 'napigkitludwigbethnicer@gmail.com',
    pass: 'password'
  }
});

var mailOptions = {
  from: 'napigkitludwigbethnicer@gmail.com',
  to: 'ludwig.napigkit@mlnfx.com',
  subject: 'Sending Email using Node.js',
  text: 'That was easy! 20181012'
};

transporter.sendMail(mailOptions, function(error, info){
  if (error) {
    console.log(error);
  } else {
    console.log('Email sent: ' + info.response);
  }
});